maze = INPUT.split("\n")
current_x = maze[0].index('|')
current_y = 0
direction = 2
answer = ""
last_is_alphabet = false
finish = false

while true
  next_char = nil
  until next_char == "+"
    case direction
    when 0 then current_y += -1
    when 1 then current_x += 1
    when 2 then current_y += 1
    when 3 then current_x += -1
    end
    next_char = maze[current_y][current_x]
    if next_char.match(/\w+/)
      answer += next_char
      last_is_alphabet = true
    elsif last_is_alphabet == true && next_char == " "
      finish = true
      break
    else
      last_is_alphabet = false
    end
  end
  break if finish == true

  direction += 1
  while true
    direction = (direction + 2) % 4
    check_char = correct_char = nil
    case direction
    when 0
      check_char = maze[current_y - 1][current_x] unless current_y == 0
      correct_char = "|"
    when 1
      check_char = maze[current_y][current_x + 1] unless current_x == maze[0].size - 1
      correct_char = "-"
    when 2
      check_char = maze[current_y + 1][current_x] unless current_y == maze.size - 1
      correct_char = "|"
    when 3
      check_char = maze[current_y][current_x - 1] unless current_x == 0
      correct_char = "-"
    end
    break if correct_char == check_char
  end
end
p answer